import React from 'react'
import { Layout } from '../layouts/Index'
import { FaqsView } from '../views/FaqsView'
import { benefitOptions } from '../Data/Contents/OptionsContents'
import { GetstarterView } from '../views/home/GetStarterView'
import { HeroView } from '../views/home/HeroView'
import ManImage from '../assets/img/man-sitting.png'
import SmallGirl from '../assets/img/home-img.png'
import PhoneGroup from '../assets/img/phone.png'
import { Link } from 'react-router-dom'
import { faqsOptions } from '../Data/Contents/FaqsContents'

const FaqsSection = () => {
    const faqsLists = faqsOptions.slice(0, 10)
    return (
        <>
            <div className="max-w-screen-xl px-4 py-16 mx-auto sm:px-6 md:px-8 lg:px-16">
                <div className="text-center pb-4">
                    <h2 className="text-2xl sm:text-3xl font-semibold py-2">Frequently Asked Questions</h2>
                    <p className="text-sm text-gray-600 font-medium">
                        Have some questions that need answers to let's answer some of your questions.
                    </p>
                </div>
                <FaqsView faqs={faqsLists} /> 
            </div> 
        </>
    )
}

const BenefitsSection = () => {
    const listOptions = benefitOptions.map((option, key) => {
        return (
            <div key={key}>
                <div className="block mt-4 text-left">
                    <div className="flex justify-start py-2" dangerouslySetInnerHTML={{__html:option.svg}}></div>
                    <p className="text-base font-semibold text-gray-900">
                        {option.title}
                    </p>
                    <p className="mt-3 text-sm text-gray-600">
                        {option.description}
                    </p>
                </div>
            </div>
        )
    })
    return (
        <>
            <div className="py-4 px-4 mx-auto sm:px-6 md:px-8 lg:px-12" style={{ background: 'linear-gradient(180deg, #F5FBFE 0%, rgba(247, 250, 255, 0) 165.54%)' }}>
                <div className="grid gap-8 sm:grid-cols-4 lg:grid-cols-4 lg:gap-x-4 lg:gap-y-12 py-4">
                    {listOptions}
                </div>
            </div>
        </>
    )
}

